import { Component,Input } from '@angular/core';
import { SharedDataService } from 'src/app/shared-data.service';


@Component({
  selector: 'app-add-edit-emp',
  templateUrl: './add-edit-emp.component.html',
  styleUrls: ['./add-edit-emp.component.css']
})
export class AddEditEmpComponent {
  @Input() dep:any
  Employee_Id:number=0
  Employee_Name:string=''
  Dataofjoin:
  constructor(private service:SharedDataService){}
  ngOnInit(){
    this.Employee_Id=this.dep.Employee_Id
    this.Employee_Name=this.dep.Employee_Name
    this.Dataofjoin=this.dep.Dataofjoin
    this.Employee_Name=this.dep.Employee_Name
  }
  ADDEP(){
    var data={
      Employee_Id:this.Employee_Id,
      Employee_Name:this.Employee_Name
    }
    this.service.AddDepartement(data).subscribe(dataa=>{
      console.log(dataa)
      alert(dataa.toString)
    })
  }
  UPDATEDEP(){
    var data={
      Employee_Id:this.Employee_Id,
      Employee_Name:this.Employee_Name
    }
    this.service.UpdateDepartement(data).subscribe(dataa=>{
      console.log(dataa)
      alert(dataa.toString)
    })
  }
}
